<template>
  <v-app>
    <div style="height: 100vh;padding:5% 1% ;direction: rtl;" class="land">
      <v-alert color="grey"> "  كلنا لفلسطين و لبنان❤️" </v-alert>
      <v-row>
        <v-col>
          <p class="text-h4 mb-2 mt-10 date">
            <!-- {{ this.d + "/ " + this.m + "/ " + this.y }} -->
              {{this.date}}
          </p>
        </v-col>
        <v-col cols="12" lg="4" md="4" sm="4">
          <p class="text-button text-success" style="translate: 0 60%;font-size: 20px !important"> {{ this.temp }} درجة
            الحرارة</p>
        </v-col>
        <v-col lg="2" md="4" sm="2" cols="12">
          <v-select return-object item-value="value" item-title="title" v-model="select"
            style="translate: 0 50%;width: 100%" :items="this.city">

          </v-select></v-col></v-row>
      <v-row>
        <v-col v-for="i in this.times" :key="i" lg="2" md="3" sm="6" cols="12">
          <v-card variant="tonal" style="
            animation-name: identifier !important;
            animation-duration: 1s;
            animation-timing-function: ease-in;
            animation-direction: alternate;
            animation-iteration-count: infinite;
            text-align: center;
            height: 60vh;
            min-height: 200px;
            transition: 0.4s;
          " class="cr">
            <v-card-title class="text-primary text-h3">
              {{ i.name }}
            </v-card-title>
            <v-card-text class="text-button" style="text-align: center">
              {{ i.time }}
            </v-card-text>
            <img style="width:100%; height:300px" :src="i.src" alt="">
          </v-card>
        </v-col>
      </v-row>
    </div>
  </v-app>
</template>

<script>
import axios from 'axios';
export default {
  name: "HomeView",
  components: {},
  async created() {
    await axios.get(`https://api.aladhan.com/v1/timingsByCity?country=sy&city=${this.select.value}`).then((res) => {
      this.times[0].time = res.data.data.timings.Fajr
      this.times[1].time = res.data.data.timings.Sunrise
      this.times[2].time = res.data.data.timings.Dhuhr
      this.times[3].time = res.data.data.timings.Asr
      this.times[4].time = res.data.data.timings.Sunset
      this.times[5].time = res.data.data.timings.Isha
    });
    await axios.get(`https://api.weatherapi.com/v1/current.json?key=970642c6e975447aabc215634241910&q=${this.select.value}&aqi=yes`).then((res) => { this.temp = res.data.current.temp_c })
  },
  mounted: function () {
    this.getDateInWords();
    const time = new Date();
    this.m = time.getMonth() + 1;
    this.y = time.getFullYear();
    this.d = time.getDate();

  },
  watch: {
    async select(newValue, oldValue) {
      console.log(newValue);
      await axios.get(`https://api.aladhan.com/v1/timingsByCity?country=sy&city=${newValue.value}`).then((res) => {
        console.log(res)
        this.times[0].time = res.data.data.timings.Fajr
        this.times[1].time = res.data.data.timings.Sunrise
        this.times[2].time = res.data.data.timings.Dhuhr
        this.times[3].time = res.data.data.timings.Asr
        this.times[4].time = res.data.data.timings.Sunset
        this.times[5].time = res.data.data.timings.Isha
      });
      await axios.get(`https://api.weatherapi.com/v1/current.json?key=970642c6e975447aabc215634241910&q=${newValue.value}&aqi=yes`).then((res) => { this.temp = res.data.current.temp_c })
    }
  },
  data: function () {
    return {
      temp: "",
      select: { title: "دمشق", value: "Damascus" },
      darkMode: false,
      times: [
        { name: "الفجر", time: "", src: "data:image/jpeg;base64," },
        { name: "الشروق", time: "", src: "data:image/jpeg;base64," },
        { name: "الظهر", time: "", src: "data:image/jpeg;base64," },
        { name: "العصر", time: "", src: "data:image/jpeg;base64," },
        { name: "المغرب", time: "", src: "data:image/jpeg;base64," },
        { name: "العشاء", time: "", src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxM3P3UhhFOFL_qvILMUFO41tZ2VHSJzOnLQ&s" }],
      m: 0,
      d: 0,
      y: 0,
      date:"",
      city: [
        { title: "دمشق", value: "Damascus" },
        { title: "حمص", value: "Homs" },
        { title: "حلب", value: "Aleppo" },
        { title: "حماة", value: "hama" },
        { title: "درعا", value: "daraa" },
        { title: "اللاذقية", value: "latakia" },
        { title: "القنيطرة", value: "Quneitra" },
        { title: "السويداء", value: "sweidaa" },
        { title: "طرطوس", value: "tartus" },
        { title: "دير الزر", value: "DeirEzzor" },
        { title: "الرقة", value: "Raqqa" },
        { title: "الحسكة", value: "Hasakah" },
        { title: "ادلب", value: "Idlib" },
      ],
      arr: [],

    };
  },
  methods: {
    getDateInWords: function () {
  const daysOfWeek = ['الأحد', 'الاثنين', 'الثلاثاء', 'الأربعاء', 'الخميس', 'الجمعة', 'السبت'];
  const months = ['يناير', 'فبراير', 'مارس', 'أبريل', 'مايو', 'يونيو', 'يوليو', 'أغسطس', 'سبتمبر', 'أكتوبر', 'نوفمبر', 'ديسمبر'];

  const
    date = new Date();
  const dayOfWeek = daysOfWeek[date.getDay()];
  const dayOfMonth = date.getDate();
  const month = months[date.getMonth()];
  const year = date.getFullYear();
  this.date =   `${dayOfWeek} ${dayOfMonth} ${month} ${year}`;
}

},
}
</script>


<style>
@keyframes identifier {
  0% {
    transform: translateY(10px);
  }

  50% {
    ransform: translateY(-10px);
  }
}

